import React, { useState } from "react";
import { NavList } from "../constant";

const MegaMenu = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [MenuList, setMenuList] = useState([]);
  const MenuClick = (name) => {
    const menu = NavList.filter((c) => c.name == name)[0]?.Children;
    console.log(menu);
    setMenuList(menu);
    setIsOpen(!isOpen);
  };
  return (
    <header className="bg-white shadow">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between h-16 items-center">
          <div className="flex-shrink-0 flex items-center">
            <span className="text-base font-bold">
              WeChat Store Manager Edition
            </span>
          </div>
          <div className="hidden sm:-my-px sm:ml-6 sm:flex sm:space-x-8">
            {/* <button
              onClick={() => setIsOpen(!isOpen)}
              className="border-b-2 border-transparent text-gray-500 hover:border-gray-300 hover:text-gray-700 inline-flex items-center px-1 pt-1 text-sm"
            >
              Products and Services
            </button> */}
            {NavList.map((item, index) => (
              <div
                onClick={() => MenuClick(item.name)}
                key={index}
                className="border-b-2 border-transparent cursor-pointer   text-black hover:border-blue-600 hover:text-blue-600 hover:font-medium   items-center  text-xs py-2 "
              >
                {item.name}
              </div>
            ))}
          </div>
          <div className="hidden sm:ml-6 sm:flex sm:items-center">
            <button className="text-gray-500 hover:text-gray-700 border px-4   border-red-600 rounded">
              Log in
            </button>
          </div>
        </div>
      </div>

      {isOpen && (
        <div className="absolute top-16 inset-x-0 bg-white shadow-lg duration-700 translate-y-0">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
            <div className="grid grid-cols-3  gap-6">
              {MenuList.map((item, i) => (
                <ul className="mt-4 " key={i}>
                  <a href="#" className="text-gray-500 hover:text-gray-900">
                    <div className="flex space-x-2">
                      <div></div>
                      <div>
                        <p className="font-semibold"> {item.name}</p>
                        <p> {item.Title}</p>
                      </div>
                    </div>
                  </a>
                </ul>
              ))}
            </div>
          </div>
        </div>
      )}
    </header>
  );
};

export default MegaMenu;
